	function getAxis(src, dst, color) {
	  const geom = new Geometry();
	  const mat = new LineBasicMaterial({ linewidth: 3, color });

	  geom.vertices.push(rescaleVector(src).clone());
	  geom.vertices.push(rescaleVector(dst).clone());

	  const axis = new Line(geom, mat, LineSegments);
	  axis.computeLineDistances();
	  return axis;
	}

	function getAxes() {
	  return [
	    getAxis(new Vector3(0, 0, 0), new Vector3(3, 0, 0), 0xff0000),
	    getAxis(new Vector3(0, 0, 0), new Vector3(0, 3, 0), 0x00ff00),
	    getAxis(new Vector3(0, 0, 0), new Vector3(0, 0, 3), 0x0000ff),
	  ];
	}

